import Plane from "./Plane";
import { useEffect, useState, useHistory } from "react";
const SeatSelect = ({}) => {
  const [flightNumber, setFlightNumber] = useState("");
  // const [customer, setCustomer] = useState({});
  // const [customerStatus, setCustomerStatus] = useState("loading");
  const [cxFirstName, setCxFirstName] = useState("");
  const [selectedSeat, setSelectedSeat] = useState(null);


  const handleSubmit = (ev) => {
    ev.preventDefault();
    if (cxFirstName) {
      fetch(`/api/add-reservation`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
        },
        body: JSON.stringify({ cxFirstName: cxFirstName }),
      })
        .then((res) => res.json())
        .then((response) => {
          if (response.status === 200) {
            history.push("/confirmed");
          }
        });
    }
  };

  return (
    <>
      <form>
        <label for="flights">
          FLIGHT NUMBER
          <select onChange={(event) => setFlightNumber(event.target.value)}>
            <option value="">Choose a Flight</option>
            <option value="SA231">SA231</option>
          </select>
        </label>
      </form>
      <h2>Select your seat and Provide your information!</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="First Name"
          value={cxFirstName}
          onChange={(ev) => setCxFirstName(ev.target.value)}
        />
        <input type="text" placeholder="Last Name" />
        <input type="text" placeholder="Email" />
        <button type="submit">Confirm</button>
      </form>
      <Plane flightNumber={flightNumber} selectedSeat={selectedSeat} />
    </>
  );
};

export default SeatSelect;
