import styled from "styled-components";
import { useState, useEffect } from "react";
const ReservationPage = () => {
  const [status, setStatus] = useState("loading");
  const [reservationInfo, setReservationInfo] = useState(null);
  const reservationNum = localStorage.getItem("_id");
  console.log(reservationNum);

  useEffect(() => {
    setStatus("loading");
    fetch(`/api/get-reservation/${reservationNum}`)
      .then((res) => res.json())
      .then((response) => {
        setReservationInfo(response.reservation);
        setStatus("idle");
      });
  }, [reservationNum]);

  return (
    <Wrapper>
      {status === "idle" && (
        <Div>
          <Form>
            Your flight is confirmed!
            <div>
              <ul>
                <li>Reservation #: {reservationInfo._id}</li>
                <li>Flight #: {reservationInfo.flight}</li>
                <li>Seat #: {reservationInfo.seat}</li>
                <li>
                  Name: {reservationInfo.givenName} {reservationInfo.surname}
                </li>
                <li>Email: {reservationInfo.email}</li>
              </ul>
            </div>
          </Form>
        </Div>
      )}
    </Wrapper>
  );
};
const Wrapper = styled.div``;
const Div = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: calc(100vh - 200px);
`;
const Form = styled.form`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  border: 5px solid var(--color-cadmium-red);
  border-radius: 5px;
  min-width: 400px;
  max-height: 300px;
  padding: 30px;
`;
const Message = styled.h2`
border`
export default ReservationPage;
