import styled from "styled-components";

const ReservationPage = () => {
  const [status, setStatus] = useState("loading");
  const [reservationInfo, setReservationInfo] = useState(null);
  const reservationNum = sessionStorage.getItem("_id");
  console.log(reservationNum);

  useEffect(() => {
    setStatus("loading");
    fetch(`/api/get-reservation/${reservationNum}`)
      .then((res) => res.json())
      .then((response) => {
        setReservationInfo(response.reservation);
        // console.log(response.reservation);
        setStatus("idle");
      });
  }, [reservationNum]);

  return (
    <Wrapper>
      {status === "idle" && (
        <div>
          Your flight is confirmed!
          <div>
            <ul>
              <li>Reservation #: {reservationInfo._id}</li>
              <li>Flight #: {reservationInfo.flight}</li>
              <li>Seat #: {reservationInfo.seat}</li>
              <li>
                Name: {reservationInfo.givenName} {reservationInfo.surname}
              </li>
              <li>Email: {reservationInfo.email}</li>
            </ul>
          </div>
        </div>
      )}
    </Wrapper>
  );
};
const Wrapper = styled.div``;
export default ReservationPage;
